  saveLiCondition.classList.add("saveLiParaStyle");

  saveLiLoc.textContent = saveLiLocText;
  saveLiTemp.textContent = saveLiTempText;
  saveLiWind.textContent = saveLiWindText;
  saveLiHumidity.textContent = saveLiHumidityText;
